function parseConfigFile($filepath, &$defaults)
{
    if (!file_exists($filepath)) {
        return;
    }

    $handle = fopen($filepath, 'r');
    if (!$handle) {
        return;
    }

    $currentSection = '';
    while (($line = fgets($handle)) !== false) {
        $line = trim($line);

        if (empty($line) || strpos($line, '#') === 0) {
            continue;
        }

        if (substr($line, -1) === ':') {
            $currentSection = substr($line, 0, -1);
            continue;
        }

        $parts = explode(':', $line, 2);
        if (count($parts) !== 2) {
            continue;
        }

        $key = trim($parts[0]);
        $value = trim($parts[1]);

        $value = trim($value, '"\'');

        if (strtolower($value) === 'true') {
            $value = true;
        } elseif (strtolower($value) === 'false') {
            $value = false;
        } elseif (is_numeric($value)) {
            if (strpos($value, '.') !== false) {
                $value = (float)$value;
            } else {
                $value = (int)$value;
            }
        }

        $fullKey = "$currentSection.$key";

        if (array_key_exists($fullKey, I360_AIBOLIT_MAP)) {
            if ($fullKey === 'MALWARE_SCAN_INTENSITY.user_scan_ram') {
                $value = $value . 'M';
            }
            $defaults[I360_AIBOLIT_MAP[$fullKey]] = $value;
        }
    }

    fclose($handle);
}

