class MDSCMSConfigFilter
{
    private $followSymlink = true;
    private $config;

    public function __construct(MDSTablesConfig $config = null)
    {
        $this->config = $config;
    }

    private function fileExistsAndNotNull(FileInfo $file)
    {
        return ($file->is_file() && $file->file_exists() && ($file->getSize() > 1000));
    }

    /**
     *
     * @param FileInfo $file
     * @param $only_dir
     * @return bool
     */

    public function needToScan(FileInfo $file, $only_dir = false)
    {
        if ($file->is_dir()) {
            return true;
        }
        if ($this->fileExistsAndNotNull($file) && $this->fileInArrayRecursive($file, $this->config->getSupportedConfigFiles())) {
            return true;
        }
        return false;
    }

    /**
     * @param $file
     * @param $haystack
     * @return bool
     */
    private function fileInArrayRecursive($file, $haystack): bool
    {
        foreach ($haystack as $value) {
            if (is_array($value)) {
                if ($this->fileInArrayRecursive($file, $value)) {
                    return true;
                }
            } elseif ($this->compareWithSupportedConfigFiles($file->getFilename(), $value)) {
                return true;
            }
        }
        return false;
    }

    /**
     * @param $filename
     * @param $configPath
     * @return bool
     */
    private function compareWithSupportedConfigFiles($filename, $configPath): bool
    {
        $configBase = basename($configPath);
        $fileBase = basename($filename);

        if ($configBase !== $fileBase) {
            return false;
        }

        return substr($filename, -strlen($configPath)) === $configPath;
    }

    /**
     * @return bool
     */
    public function isFollowSymlink()
    {
        return $this->followSymlink;
    }
}

