class MDSCMSAddon
{
    const CONFIG_FILE = '';
    const MIN_SIZE = '1000';

    protected $app;
    protected $path;

    public function __construct($path)
    {
        if (is_array(static::CONFIG_FILE)) {
            foreach (static::CONFIG_FILE as $config_file) {
                $this->path = $this->getPath($path, $config_file);
                if ($this->path) {
                    break;
                }
            }
            if (!$this->path) {
                throw new MDSException(MDSErrors::MDS_INVALID_CMS_CONFIG, $path . '/' . implode("|",static::CONFIG_FILE));
            }
        } else {
            $this->path = $this->getPath($path, static::CONFIG_FILE);
            if (!$this->path) {
                throw new MDSException(MDSErrors::MDS_INVALID_CMS_CONFIG, $path . '/' . static::CONFIG_FILE);
            }
        }
    }

    /**
     * @param $path
     * @param $config_files
     * @return false|string
     */
    private function getPath($path, $config_files)
    {
        $path = rtrim($path, '/');
        $config_files = ltrim($config_files, '/');

        if (strpos($config_files, '/') !== false) {
            $config_dir = dirname($config_files);

            if (substr($path, -strlen($config_dir)) === $config_dir) {
                $fullPath = $path . '/' . basename($config_files);
            } else {
                $fullPath = $path . '/' . $config_files;
            }
            if ($this->isValidFile($fullPath)) {
                return $this->removeEndingPath($fullPath,$config_files);
            }
        } else {
            if ($this->isValidFile($path . '/' . static::CONFIG_FILE)) {
                return $path;
            }
        }

        return false;
    }
    private function isValidFile(string $filePath): bool
    {
        return file_exists($filePath) && is_readable($filePath) && filesize($filePath) >= self::MIN_SIZE;
    }

    /**
     * @param string $fullPath
     * @param string $ending
     * @return false|string|null
     */
    function removeEndingPath(string $fullPath, string $configRelativePath)
    {
        $fullPath = rtrim($fullPath, DIRECTORY_SEPARATOR);
        $configRelativePath = ltrim($configRelativePath, DIRECTORY_SEPARATOR);

        if (substr($fullPath, -strlen($configRelativePath)) === $configRelativePath) {
            $basePath = substr($fullPath, 0, -strlen($configRelativePath));
            return rtrim($basePath, DIRECTORY_SEPARATOR);
        }

        return false;
    }
    public function parseConfig()
    {
    }
}

class MDSAVDPathFilter
{
    private $ignoreList = [];

    public function __construct($filepath)
    {
        if (!file_exists($filepath) || !is_file($filepath) || !is_readable($filepath)) {
            return;
        }

        $content = file_get_contents($filepath);
        $list = explode("\n", $content);
        foreach ($list as $base64_filepath) {
            if ($base64_filepath !== '') {
                $this->ignoreList[$base64_filepath] = '';
            }
        }
    }

    public function needToScan($file)
    {
        $tree = $this->getTree($file);
        if ($this->pathRelatesTo($tree, $this->ignoreList, true)) {
            return false;
        }
        return true;
    }

    private function getTree($file)
    {
        $tree = [];
        $path = $file;
        while ($path !== '.' && $path !== '/') {
            $path = dirname($path, 1);
            $tree[] = $path;
        }
        $tree[] = $file;
        return $tree;
    }

    private function pathRelatesTo($tree, $pathes, $base64 = false)
    {
        foreach ($tree as $path) {
            if ($base64) {
                $path = base64_encode($path);
            }
            if (isset($pathes[$path])) {
                return true;
            }
        }
        return false;
    }
}

